-- Bulk INSERT into public.tickets from support_tickets_upload.tsv
-- Run TICKETS_ADD_SUPPORT_COLUMNS.sql first. Requires at least one company and one user_profiles row.

BEGIN;
INSERT INTO public.tickets (
  reference_no, title, description, type, status, priority,
  company_id, division_id, created_by, created_at, updated_at,
  attachment_url, page, company_name, division, division_other, user_name,
  communicated_through, submitted_by, query_arrival_at, quality_of_response,
  customer_questions, query_response_at
) VALUES
  ('BU-001', 'Showing POP Up to select Item, when already selected', 'In Pending PO GRN when one item is already selected, we are going to make an GRN, pop up showing as "Please select an item." Old Ref: BU-0001', 'bug', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-11-08 14:48:07+00'::TIMESTAMPTZ, NOW(), NULL, 'Pending PO (GRN)', 'Demo_c', 'All', NULL, 'Shreyasi Mukherjee', 'phone', 'Shreyasi', '2024-11-08 14:48:07+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('BU-002', 'Reorder Level add', 'When creating an item reorder level, we can add multiple entries for a single division, which is not necessary. In Demo_C, we have three divisions, so we should only be able to add one entry per division. However, currently, it allows adding multiple entries for the same division at once. Old Ref: BU-0002', 'bug', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-11-08 14:51:07+00'::TIMESTAMPTZ, NOW(), NULL, 'ITEM', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-11-08 14:51:07+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-001', 'Attachment file not cancel by single click', 'When we attached one file on creating Indent, then add another attachment click after that we can not remove it clicking cross sign on first click 2 times clicking then the another file is remove Old Ref: CH-0001', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-11-09 14:53:05+00'::TIMESTAMPTZ, NOW(), NULL, 'Create Indent', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-11-09 14:53:05+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-002', 'Location column should be added in Stock Adjustment Aprroval', 'Old Ref: CH-0009', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-06 15:13:59+00'::TIMESTAMPTZ, NOW(), NULL, 'Stock Adjustment Approvals', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-12-06 15:13:59+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-003', 'There should be an option to choose a location.', 'On the "Issue Tools on Returnable Basis" page, there should be an option to choose the location. Otherwise, users will have to update the location repeatedly on the Item Stock page after returning the items. Old Ref: CH-0011', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-09 15:18:05+00'::TIMESTAMPTZ, NOW(), NULL, 'Issue Tools on Returnable Basis', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-12-09 15:18:05+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-004', 'Tag to Repair--> option to choose quantity', 'There is no option to choose a specific quantity. If an item has 3 quantities, and we want to tag 1 quantity for repair and 2 quantities for scrap, there should be an option to do so. Old Ref: CH-0018', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-17 15:38:00+00'::TIMESTAMPTZ, NOW(), NULL, 'Items Pending to be sent for repair', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-12-17 15:38:00+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-005', 'Sometimes item for repair divided in three part, sent for repair, scrap, remaining', 'The system should behave correctly when an item is pending for repair. For a total quantity of 10, with 2 sent for repair, 6 marked as scrap, and 2 remaining, all information should be accurately reflected. Old Ref: CH-0021', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-17 15:42:41+00'::TIMESTAMPTZ, NOW(), NULL, 'Items Pending to be sent for repair', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2024-12-17 15:42:41+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-006', 'If the indenter provides an attachment in work order indent , and the purchaser wants to send that attachment to the vendor or add any additional attachment, same as the PO, but no attachment could not be sent to the vendor. "(Work Order)"', 'Old Ref: CH-0025', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-24 15:48:48+00'::TIMESTAMPTZ, NOW(), NULL, 'Vendor Quote', 'Demo_c', 'All', NULL, 'Rimpa Manik', NULL, 'Aman', '2024-12-24 15:48:48+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-007', 'Rate is not showing on WRN entry page.', 'Old Ref: CH-0026', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2024-12-24 15:50:33+00'::TIMESTAMPTZ, NOW(), NULL, 'WRN Entry', 'Demo_c', 'All', NULL, 'Aman Dey', NULL, 'Aman', '2024-12-24 15:50:33+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-008', 'Problem in creating Work Order Indent', 'When entering a single WO Indent as input, if the quantity field is left blank or set to zero, a proper message appears just under the quantity box stating "Invalid qty." However, the following issues occur when providing multiple inputs in a single WO Indent: 1. If the first input in the WO Indent is filled correctly with all fields, but the quantity field is filled incorrectly for subsequent inputs, an error message appears stating "Something went wrong." 2. If the first input in the WO Indent has an incorrect quantity, is blank, or is set to zero, and all subsequent inputs are filled correctly, the message "Invalid qty" appears under the quantity box for all inputs in that WO Indent, including both the incorrectly and correctly filled inputs. Old Ref: CH-0036', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-22 13:26:11+00'::TIMESTAMPTZ, NOW(), NULL, 'Create Wo Indent', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-22 13:26:11+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-009', 'Problem in creating Work Order Indent', 'If we create a WO Indent with a blank title, the *Create* button does not work. However, no message is displayed under the title box, such as "Invalid input," "This field is mandatory," or "Please provide input using A-Z, a-z, or 0-9." Old Ref: CH-0037', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-22 13:49:40+00'::TIMESTAMPTZ, NOW(), NULL, 'Create Wo Indent', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-22 13:49:40+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-010', 'Pending WO Indent RFQ page -->Description filter', 'The Description filter is providing an extra option, labeled as "blank." If we select "blank" as a filter option, it does not return the correct results. Additionally, if we select any specific item or multiple options from the filter without choosing "blank," after applying the filter, it automatically selects the "blank" option as an extra filter. However, the search results display only the items intentionally selected in the filter. (Refer to the attached video titled "Pending WO Indent Description Filter Glitch" for a detailed demonstration.) Old Ref: CH-0041', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-22 17:52:04+00'::TIMESTAMPTZ, NOW(), 'https://1drv.ms/v/c/a87322d1a582cf6a/Ecc-7qC_7_BLiXZMkSinTWUBev9EB8SVXcULAfgQTS2U3Q?e=woej2t', 'Pending Work Order Indent RFQ', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-22 17:52:04+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-011', 'Pending WO Indent RFQ page -->Requester filter', 'The filter options for the *Requester* column are being displayed repeatedly. For example, "Demo endUser" is an Indenter in our system across all divisions. When filtering by the requester name "Demo endUser," it should display all search results requested by this user. However, the filter is showing multiple options with the same name. One option reflects all search results for this requester, while other options with the same name display division-wise search results. Old Ref: CH-0042', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 11:24:28+00'::TIMESTAMPTZ, NOW(), NULL, 'Pending Work Order Indent RFQ', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 11:24:28+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-012', 'Whatsapp message after vendor submitted quotation is wholly wrong', 'For WORFQ, after furnishing the RFQ, the purchaser should receive a message stating, "Quote furnished by ...". Instead, the vendor is receiving this message. Additionally, the message displays "WORFQ/" without showing the RFQ serial number after the slash (/), which should be corrected to include the serial number. Old Ref: CH-0044', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 12:25:16+00'::TIMESTAMPTZ, NOW(), NULL, 'Vendor Quote', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 12:25:16+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-013', 'Pending WO Indent RFQ page', 'On the Pending WO Indents page, an RFQ was first sent to Vendor A with RFQ number WORFQ/93, which is displayed on this page. Subsequently, a WO was created for Vendor B for the same Work Order Indent WOIND/24-25/00054, with RFQ number WORFQ/94, but it is not appearing in the pending list. For Work Order Indent WOIND/24-25/00054 in DemoWarehouse, it shows "under vendors 0/2", and when clicked, it displays only Vendor A and indicates that Vendor B has not quoted. However, a WO has already been created for Vendor B, and it is still pending approval. Old Ref: CH-0046', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 13:40:44+00'::TIMESTAMPTZ, NOW(), NULL, 'Pending Work Order Indent RFQ', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 13:40:44+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-014', 'Pending WO Indent RFQ page', 'The first time we sent WORFQ to Vendor A, WORFQ/95 was successfully sent. However, when we attempted to send WORFQ to the same vendor a second time, it was also successfully sent with the same WORFQ/95. Ideally, the system should notify that WORFQ/95 has already been sent to Vendor A. Old Ref: CH-0047', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 13:45:05+00'::TIMESTAMPTZ, NOW(), NULL, 'Pending Work Order Indent RFQ', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 13:45:05+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-015', 'WRN entry--> Qty.', 'When creating a WRN, if the WRN quantity is set to 0, the WRN preview incorrectly shows 1 quantity. However, upon confirmation, an error message appears stating "Something went wrong." Instead, the system should display an error message in the WRN stating "Invalid quantity" when 0 quantity is entered. Old Ref: CH-0049', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 13:49:07+00'::TIMESTAMPTZ, NOW(), NULL, 'WRN Entry', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 13:49:07+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-016', 'WRN entry--> Qty.', 'When creating a WRN, if the input WRN quantity exceeds the WO quantity and the WRN Preview button is clicked, the button works, but the WRN preview is not displayed due to the incorrect quantity. Instead, the system should display an error message indicating the quantity is invalid or exceeds the WO quantity. Old Ref: CH-0050', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-23 13:52:03+00'::TIMESTAMPTZ, NOW(), NULL, 'WRN Entry', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-23 13:52:03+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-017', 'Link for Quote functioning different in Whatsapp and Email(Same like FMS 13)', 'The vendor received a reminder message for a quote via WhatsApp and email after the WO RFQ. When the vendor clicks the link to submit a quote from WhatsApp, it shows the message "PO already made for this RFQ." However, when the same link is accessed through email, the vendor can submit their quote. Old Ref: CH-0058', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-01-24 09:35:36+00'::TIMESTAMPTZ, NOW(), NULL, 'Whatsapp IndustryPrime', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-01-24 09:35:36+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('BU-003', 'When creating a manual quote or when the vendor is quoting, if user copy the previous quote, the previous price gets included in the HSN Code.', 'Old Ref: BU-0011', 'bug', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-05 10:16:20+00'::TIMESTAMPTZ, NOW(), NULL, 'Vendor Quote', 'Demo_c', 'All', NULL, 'Aman', 'phone', 'Aman', '2025-02-05 10:16:20+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-018', 'Boundation should be applied on date range', 'Stockledger date range choose- start date 1/1/2026  end date 6/2/2025 report received in mail there should be bounde the date range and not choose the future date Old Ref: CH-0097', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-06 10:45:26+00'::TIMESTAMPTZ, NOW(), NULL, 'Stock Ledger', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-06 10:45:26+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-019', 'Setup--> Make--> Blank field save', 'A blank field has been saved under Make, but it is not appearing in the list. Old Ref: CH-0108', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 12:32:45+00'::TIMESTAMPTZ, NOW(), NULL, 'Set Up', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 12:32:45+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-020', 'Style and functionality are not working properly.', 'In the page of item Group wise consumption the view of whole page is not correct like setup, transaction, etc. positions, under this user end view is Blank. in the left most corner if we chose any division it''s not showing and if we click on excel export the report is only contain two word Item Group & Amount . Old Ref: CH-0109', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 12:44:26+00'::TIMESTAMPTZ, NOW(), NULL, 'Item Group Wise Consumption', 'Demo_c', 'All', NULL, 'Aman', 'phone', 'Aman', '2025-02-07 12:44:26+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-021', 'Setup--> Cat no--> Blank field save', 'A blank field has been saved under Cat no, and also showing in catalogue list Old Ref: CH-0110', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 12:45:11+00'::TIMESTAMPTZ, NOW(), NULL, 'Set Up', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 12:45:11+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-022', 'notification--> RGP--> 0 qty accepted for return but it is still pending on RGP', 'Old Ref: CH-0111', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 12:47:57+00'::TIMESTAMPTZ, NOW(), NULL, 'Returnable Gate Pass', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 12:47:57+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-023', 'Create Indent--> Item Name', 'When taking input as "No Existing Match" in the field and clicking the Create Indent button: - The button is working, but the Indent is not being created. - No popup or message is displayed for the "No Existing Match" item name. - The same issue occurs when adding another item with correct details while one item has "No Existing Match"—the indent is still not created. Old Ref: CH-0113', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 12:52:58+00'::TIMESTAMPTZ, NOW(), NULL, 'Create Indent', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 12:52:58+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-024', 'GRN entry--> accepted qty & rejected qty', 'If we enter Accepted Qty as "-2" and Rejected Qty as "0": - The system still shows the GRN preview. - When clicking Confirm, it moves to the Pending PO GRN page. - However, the GRN is not created and an error message appears: "Give appropriate qty." Old Ref: CH-0114', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 13:01:22+00'::TIMESTAMPTZ, NOW(), NULL, 'GRN Entry', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 13:01:22+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-025', 'GRN entry--> accepted qty & rejected qty', 'If we enter Accepted Qty as a negative value and the quantity exceeds the PO Qty, while keeping Rejected Qty as "0": - The system still shows the GRN preview, even though the input is incorrect. - When clicking Confirm, it moves to the Pending PO GRN page. - However, the GRN is not created and an error message appears: "Give appropriate qty." This indicates that the system allows an incorrect preview but prevents the final creation of the GRN due to invalid quantities. Old Ref: CH-0115', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 13:04:20+00'::TIMESTAMPTZ, NOW(), NULL, 'GRN Entry', 'Demo_c', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 13:04:20+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-026', 'WRN entry--> accepted qty', 'If we enter 0 in the Accepted Qty field: - The system still shows the GRN preview, even though the input is invalid. - When clicking Confirm, an error message appears: "Something went wrong." - The GRN is not created, but the system does not provide a clear reason for the failure. This suggests that the system should ideally validate the Accepted Qty before showing the preview and provide a more specific error message. Old Ref: CH-0116', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 13:06:34+00'::TIMESTAMPTZ, NOW(), NULL, 'WRN Entry', 'Ghankun Steel Pvt Ltd (SMS Division)', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 13:06:34+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-027', 'Items pending to be sent for repair--> tag to scrap', 'Currently, when items pending to be sent for repair are tagged to scrap, the system allows the item to be added to the Scrap Register even if Scrap Weight and Scrap Qty are not provided. Note: The system should not allow the item to be added to the Scrap Register unless both Scrap Weight and Scrap Qty are entered. Instead, it should prompt an error message requesting the missing input. Old Ref: CH-0117', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-07 13:08:48+00'::TIMESTAMPTZ, NOW(), NULL, 'Items Pending to be sent for repair', 'Crescent Foundry', 'All', NULL, 'Rimpa Manik', 'phone', 'Rimpa', '2025-02-07 13:08:48+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('BU-004', 'When increasing the quantity on the approval page.', 'If we issue a quantity of 1 for a repairable item, and before approval, the quantity on the site increases to 3, then when we send it for repair using the Tag To Repair option, But in the Returnable Items Not Yet Received section still shows the quantity as 1, indicating that it has been sent for repair. Old Ref: BU-0014', 'bug', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-02-10 11:30:45+00'::TIMESTAMPTZ, NOW(), NULL, 'Returnable Items Not Yet Received', 'Demo_c', 'All', NULL, 'Aman', 'phone', 'Aman', '2025-02-10 11:30:45+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-028', 'Change the company name', 'Change the company name from Ghankun Steel Pvt. Ltd. (SMS Division) to Ghankun Steel Melting Pvt. Ltd. Date change from 22/04/2025 only change company name division will be SMS. For other details see the attachment Old Ref: CH-0336', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-04-23 13:39:19+00'::TIMESTAMPTZ, NOW(), 'https://drive.google.com/open?id=1NfezdHO0a2fNfjSTUDDMGbbC2tSGZviI', 'Dashboard', 'Kodarma Chemical Pvt. Ltd.', 'SMS', NULL, 'O P Sahu', NULL, 'Rimpa', '2025-04-23 13:39:19+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-029', 'Rearrange the report highest to lowest', 'In the report Company Wise Approved Quotes all the Qty, Amount, Avg rate should be reflect from highest to lowest. Old Ref: CH-0365', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-05-03 09:56:24+00'::TIMESTAMPTZ, NOW(), NULL, 'Company Wise Approved Quotes', 'Demo_c', 'All', NULL, 'Internal Auditor (Sidharth Dhanuka)', 'phone', 'Rimpa', '2025-05-03 09:56:24+00'::TIMESTAMPTZ, NULL, NULL, NULL),
  ('CH-030', 'Error PopUp', 'When we do not select on vendor (L1) and tap on create PO, it does not create PO which is fine but user is unable to figure out why it is not happening since a message like "Please select the vendor" is not popping up Old Ref: CH-0396', 'chore', 'open', 'medium', (SELECT id FROM public.companies ORDER BY id ASC LIMIT 1), NULL, (SELECT id FROM public.user_profiles ORDER BY id ASC LIMIT 1), '2025-05-13 10:15:49+00'::TIMESTAMPTZ, NOW(), NULL, 'Quotation Comparison', 'Demo_c', 'All', NULL, 'SHUBHAM KEJRIWAL', 'phone', 'Aman', '2025-05-13 10:15:49+00'::TIMESTAMPTZ, NULL, NULL, NULL),
